import axios from "axios";
import { useState } from "react";
import "./UploadImage.css";

function UploadImage(props) {
  const [originalImage, changeOriginalImage] = useState("");
  const [rotatedImage, changeRotatedImage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  return (
    <div className={"container"}>
      <input
        type="file"
        id="myFile"
        name="filename"
        accept="image/*"
        onChange={(e) =>
          handleImageUpload(
            e,
            changeOriginalImage,
            changeRotatedImage,
            setErrorMessage
          )
        }
      />
      {errorMessage != "" && <p>{errorMessage}</p>}
      <div className={"image-section"}>
        {originalImage != "" && (
          <img
            src={originalImage}
            // style={{ maxWidth: "50%", marginTop: "20px" }}
          />
        )}
        {rotatedImage != "" && (
          <img
            src={rotatedImage}
            // style={{ maxWidth: "50%", marginTop: "20px" }}
          />
        )}
      </div>
    </div>
  );
}

function handleImageUpload(
  e,
  changeOriginalImage,
  changeRotatedImage,
  setErrorMessage
) {
  let selectedImage = e.target.files[0];
  const selectedImageURL = URL.createObjectURL(selectedImage);
  if (selectedImage.size > 5000000) {
    setErrorMessage(
      "Image size is too big. Try again with an image size less than 5MB."
    );
  } else {
    changeOriginalImage(selectedImageURL);
    setErrorMessage("");
    saveImage(selectedImage, selectedImageURL, changeRotatedImage);
  }
}

async function saveImage(selectedImage, selectedImageURL, changeRotatedImage) {
  const toBase64 = (file) =>
    new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
    });

  let data = {
    imageFile: await toBase64(selectedImage),
  };

  //   await fetch("/api/endpoint", {
  //     method: "POST",
  //     headers: new Headers({
  //       "Content-Type": "application/json",
  //     }),
  //     body: JSON.stringify(data),
  //   })
  console.log(selectedImage);
  axios
    .post(`http://localhost:3001/uploadImage`, data)
    // axios({
    //     method: 'post',
    //     url: 'http://localhost:3001/uploadImage',
    //     headers: {'Content-Type' : 'application/json'},
    //     body: {"a": 1}
    // })
    .then(async (res) => {
      let resJSON = await res.data;
      console.log(resJSON);
      changeRotatedImage(resJSON.rotatedImage);
      //   let url = resJSON.url;
      console.log("success");
    })
    .catch((error) => {
      console.log(error);
      console.log("upload failed.");
    });
}

export default UploadImage;
