import axios from "axios";
import { useState } from "react";
import "./UploadImage.css";

function UploadImage(props) {
  const [image, changeImage] = useState("");
  const [rotatedImage, changeRotatedImage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [numDegrees, setNumDegrees] = useState(90);
  const [showInput, setShowInput] = useState(false);
  const [selectedImage, setSelectedImage] = useState({})
  return (
    <div className={"container"}>
      <div className={"input-container"}>
        Choose an Image
        <input
          type="file"
          id="myFile"
          name="filename"
          accept="image/*"
          onChange={(e) => handleImageUpload(e, changeImage, setErrorMessage, setSelectedImage)}
          className={"upload-button"}
        />
      </div>

      {errorMessage != "" && <p>{errorMessage}</p>}
      <div className={"image-section"}>
        {image != "" && (
          <img src={image} style={{ maxWidth: "50%", marginTop: "20px" }} />
        )}
      </div>
      {!showInput && (
        <div className={"button-section"}>
          {image != "" && (
            <div
              className={"input-container"}
              onClick={() => setShowInput(!showInput)}
            >
              Rotate Image
            </div>
          )}
          {image != "" && <div className={"input-container"}>Surprise me</div>}
        </div>
      )}
      {showInput && (
        <div className={"rotate-degrees"}>
          <div>How much do you want to rotate the image by?</div>
          <input
            type="text"
            pattern="[0-9]*"
            onChange={(e) => setNumDegrees(e.target.value.replace(/\D/, ""))}
            value={numDegrees}
            style={{ marginTop: "20px" }}
          />
          <div
            className={"input-container"}
            onClick={(e) => {
              handleRotate(
                e,
                numDegrees,
                setShowInput,
                selectedImage,
                changeImage,
                setErrorMessage
              );
            }}
          >
            Rotate
          </div>
        </div>
      )}
    </div>
  );
}

function handleRotate(
  e,
  numDegrees,
  setShowInput,
  selectedImage,
  changeImage,
  setErrorMessage
) {
  setShowInput(false);
  saveImage(numDegrees, selectedImage, changeImage, setErrorMessage);
}

function handleImageUpload(e, changeImage, setErrorMessage, setSelectedImage) {
  let selectedImage = e.target.files[0];
  const selectedImageURL = URL.createObjectURL(selectedImage);
  if (selectedImage.size > 5000000) {
    setErrorMessage(
      "Image size is too big. Try again with an image size less than 5MB."
    );
  } else {
    changeImage(selectedImageURL);
    setSelectedImage(selectedImage)
    setErrorMessage("");
    // saveImage(
    //   selectedImage,
    //   selectedImageURL,
    //   changeImage,
    //   setErrorMessage
    // );
  }
}

async function saveImage(
  numDegrees,
  selectedImage,
  //   selectedImageURL,
  changeImage,
  setErrorMessage
) {
  const toBase64 = (file) =>
    new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
    });

  let data = {
    imageFile: await toBase64(selectedImage),
    numDegrees: numDegrees,
  };

  //   await fetch("/api/endpoint", {
  //     method: "POST",
  //     headers: new Headers({
  //       "Content-Type": "application/json",
  //     }),
  //     body: JSON.stringify(data),
  //   })
  console.log(selectedImage);
  axios
    .post(`http://localhost:3001/uploadImage`, data)
    // axios({
    //     method: 'post',
    //     url: 'http://localhost:3001/uploadImage',
    //     headers: {'Content-Type' : 'application/json'},
    //     body: {"a": 1}
    // })
    .then(async (res) => {
      let resJSON = await res.data;
      console.log(res);
      console.log(resJSON);
      if (res.status === 200) {
        changeImage(resJSON.rotatedImage);
        setErrorMessage("Your image was successfully rotated!");
      } else {
        setErrorMessage("");
      }
    })
    .catch((error) => {
      console.log(error);
      console.log("upload failed.");
      setErrorMessage(error);
    });
}

export default UploadImage;
