import axios from "axios";
import { useState } from "react";
import "./UploadImage.css";

function UploadImage(props) {
  const [image, changeImage] = useState(""); // image that is displayed
  const [selectedImage, setSelectedImage] = useState({});
  const [errorMessage, setErrorMessage] = useState("");
  const [numDegrees, setNumDegrees] = useState(90);
  const [showInput, setShowInput] = useState(false);
  const [loading, setLoading] = useState(false);
  return (
    <div className={"container"}>
      <div className={"input-container"}>
        Choose an Image
        <input
          type="file"
          id="myFile"
          name="filename"
          accept="image/*"
          onChange={(e) =>
            handleImageUpload(e, changeImage, setErrorMessage, setSelectedImage)
          }
          className={"upload-button"}
        />
      </div>

      {!loading && errorMessage != "" && <p>{errorMessage}</p>}
      {loading && <p>{"Loading..."}</p>}
      <div className={"image-section"}>
        {image != "" && (
          <img
            src={image}
            style={{ width: "50vw", maxWidth: "50%", marginTop: "20px" }}
          />
        )}
      </div>
      {!showInput && (
        <div className={"button-section"}>
          {image != "" && (
            <div
              className={"input-container"}
              onClick={() => setShowInput(!showInput)}
            >
              Rotate
            </div>
          )}
          {image != "" && (
            <div
              className={"input-container"}
              onClick={() =>
                handleInvert(
                  selectedImage,
                  changeImage,
                  setErrorMessage,
                  setSelectedImage
                )
              }
            >
              Invert
            </div>
          )}
          {image != "" && (
            <div
              className={"input-container"}
              onClick={() =>
                handleGreyscale(
                  selectedImage,
                  changeImage,
                  setErrorMessage,
                  setSelectedImage
                )
              }
            >
              Greyscale
            </div>
          )}
        </div>
      )}
      {showInput && (
        <div className={"rotate-degrees"}>
          <div>How much do you want to rotate the image by?</div>
          <input
            type="text"
            pattern="[0-9]*"
            onChange={(e) => setNumDegrees(e.target.value.replace(/\D/, ""))}
            value={numDegrees}
            style={{ marginTop: "20px" }}
            className={"input-degrees"}
          />
          <div
            className={"input-container"}
            onClick={() => {
              handleRotate(
                numDegrees,
                setShowInput,
                selectedImage,
                changeImage,
                setErrorMessage,
                setSelectedImage
              );
            }}
          >
            Rotate
          </div>
        </div>
      )}
    </div>
  );
}

function handleAction(
  action,
  numDegrees,
  setShowInput,
  selectedImage,
  changeImage,
  setErrorMessage,
  setSelectedImage
) {
  if (action === "rotate") {
    setShowInput(false);
    let data = {
      imageFile: selectedImage,
      numDegrees: numDegrees,
    };

    saveImage(data, action, changeImage, setErrorMessage, setSelectedImage);
  } else {
    let data = {
        imageFile: selectedImage,
      };
      saveImage(data, action, changeImage, setErrorMessage, setSelectedImage);
  }
}

