import axios from 'axios'

function UploadImage(props) {
  return (
    <input
      type="file"
      id="myFile"
      name="filename"
      accept="image/*"
      onChange={(e) => handleProfileImageUpload(e)}
    />
  );
}

function handleProfileImageUpload(e) {
  let selectedImage = e.target.files[0];
  saveProfileImage(selectedImage);
//   if (selectedImage.size > 5000000) {
//     console.log("image too big");
//   } else {
//     saveProfileImage(selectedImage);
//   }
}

async function saveProfileImage(selectedImage) {
  const toBase64 = (file) =>
    new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
    });

  let data = {
    requestedAPI: "saveProfileImage",
    imageFile: await toBase64(selectedImage),
  };

//   await fetch("/api/endpoint", {
//     method: "POST",
//     headers: new Headers({
//       "Content-Type": "application/json",
//     }),
//     body: JSON.stringify(data),
//   })
    axios.get(`http://localhost:3001/testdata`)
    // .then(res => {
    //     console.log(res.data)
    // })
    .then(async (res) => {
      let resJSON = await res.data;
      console.log(resJSON)
    //   let url = resJSON.url;
      console.log("success")
    })
    .catch((error) => {
      console.log(error);
      console.log("upload failed.");
    });
}

export default UploadImage;
